import React, { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";
import { Card, Button, CardTitle, Row, Col, Label } from "reactstrap";

import { store } from "../../redux";
import { useSelector } from "react-redux";

import * as S from "./styles";

const Application = () => {
  const history = useHistory();
  const authState = useSelector(({ authReducer }) => authReducer.user);

  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [signedCourses, setSignedCourses] = useState([]);

  useEffect(() => {
    setName(authState.name);
    setEmail(authState.email);
    setSignedCourses(authState.signedCourses);
  }, [authState]);

  const logoff = () => {
    localStorage.removeItem("token");
    history.push("/");
  };

  return (
    <S.Container className="container">
      <Row>
        <Col md="6">
          <S.Label>Name</S.Label>
          <S.UserData>{name}</S.UserData>
        </Col>
        <Col md="6">
          <S.Label>Email</S.Label>
          <S.UserData>{email}</S.UserData>
        </Col>
      </Row>
      <S.Button color="danger" onClick={logoff}>
        Logout
      </S.Button>
    </S.Container>
  );
};

export default Application;
