import React, { useEffect, useState } from "react";
import { useHistory } from "react-router-dom";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";
import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.min.css";
import {
  Card,
  Button,
  CardTitle,
  Row,
  Col,
  Form,
  FormGroup,
  Input,
} from "reactstrap";
import API from "../../api";
import ToastMessage from "../../components/ToastMessage";
import * as S from "./styles";

import { actions } from "../../redux";
import { useDispatch, useSelector } from "react-redux";

const schema = yup.object().shape({
  email: yup
    .string()
    .email("Email pattern is incorrect")
    .required("Email is required"),
  password: yup.string().required("Password is required"),
});

const Login = () => {
  const history = useHistory();
  const userState = useSelector(({ authReducer }) => authReducer);
  const dispatch = useDispatch();

  const { register, handleSubmit, errors } = useForm({
    resolver: yupResolver(schema),
  });

  useEffect(() => {
    getUser();
  });

  const getUser = async () => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        history.push("/");
      } else {
        const responseUser = await API.get("/person/me", {
          headers: {
            "X-Brand": "proenem",
            Authorization: `Bearer ${token}`,
          },
        });
        dispatch(
          actions.getUserData({
            name: responseUser.data.name,
            email: responseUser.data.email,
            signedCourses: responseUser.data.courses,
          })
        );
        history.push("/application");
      }
    } catch (e) {
      toast.error(
        <ToastMessage title="Erro" message={e.response.data.detail} />
      );
      history.push("/");
    }
  };

  const onSubmit = async ({ email, password }) => {
    try {
      const responseToken = await API.post(
        "/token",
        { email, password },
        { headers: { "X-Brand": "proenem" } }
      );

      localStorage.setItem("token", responseToken.data.token);

      if (responseToken.data.token) {
        getUser();
      }

      history.push("/application");
    } catch (e) {
      toast.error(
        <ToastMessage title="Erro" message={e.response.data.detail} />
      );
    }
  };

  return (
    <S.Container>
      <ToastContainer />
      Loading: {useState.isLoading}
      <Row>
        <Col xs={{ size: 12, offset: 0 }}>
          <Card body>
            <CardTitle tag="h5" className="text-center">
              Login
            </CardTitle>
            <Form onSubmit={handleSubmit(onSubmit)}>
              <FormGroup>
                <Input
                  name="email"
                  placeholder="Your e-mail"
                  innerRef={register}
                  invalid={!!errors.email}
                />
                <S.ErrorMessage>{errors.email?.message}</S.ErrorMessage>
              </FormGroup>
              <FormGroup>
                <Input
                  type="password"
                  name="password"
                  placeholder="Your password"
                  innerRef={register}
                  invalid={!!errors.password}
                />
                <S.ErrorMessage>{errors.password?.message}</S.ErrorMessage>
              </FormGroup>
              <Button type="submit" color="primary" block>
                Sign In
              </Button>
            </Form>
          </Card>
        </Col>
      </Row>
    </S.Container>
  );
};

export default Login;
