import React, { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";
import { useSelector } from "react-redux";
import Moment from "react-moment";
import { Button, CardTitle, Row, Col, Label } from "reactstrap";

import * as S from "./styles";

const Application = () => {
  const history = useHistory();
  const userState = useSelector(({ authReducer }) => authReducer.user);

  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [signedCourses, setSignedCourses] = useState([]);

  useEffect(() => {
    setName(userState.name);
    setEmail(userState.email);
    setSignedCourses(userState.signedCourses);
  }, []);

  const logoff = () => {
    localStorage.removeItem("token");
    history.push("/");
  };

  return (
    <S.Container className="container">
      <Row>
        <Col md="6">
          <S.Label>Name</S.Label>
          <S.UserData>{name}</S.UserData>
        </Col>
        <Col md="6">
          <S.Label>Email</S.Label>
          <S.UserData>{email}</S.UserData>
        </Col>
        <Col md="12">
          <S.Table>
            <thead>
              <tr>
                <th>Nome da turma</th>
                <th>Período</th>
              </tr>
            </thead>
            <tbody>
              {!!signedCourses.length &&
                signedCourses.map((item) => (
                  <tr>
                    <td>{item.name}</td>
                    <td>
                      <Moment format="DD/MM/YYYY">
                        {item.schoolPeriod.startDate}
                      </Moment>
                      <Moment format="DD/MM/YYYY">
                        {item.schoolPeriod.endDate}
                      </Moment>
                    </td>
                  </tr>
                ))}
              {!signedCourses.length && (
                <tr>
                  <td colSpan="2">Sem matriculas</td>
                </tr>
              )}
            </tbody>
          </S.Table>
        </Col>
      </Row>
      <S.Button color="danger" onClick={logoff}>
        Logout
      </S.Button>
    </S.Container>
  );
};

export default Application;
