import React, { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";
import { Card, Button, CardTitle, Row, Col, Label } from "reactstrap";

import { store, actions } from "../../redux";
import { useSelector, useDispatch } from "react-redux";

import * as S from "./styles";

export default function Application() {
  const [name, setName] = useState(useSelector((state) => state.name));
  const [email, setEmail] = useState("");
  const [signedCourses, setSignedCourses] = useState([]);

  const history = useHistory();

  useEffect(() => {
    store.subscribe(() => {
      setName(store.getState().applicationReducer.name);
      setEmail(store.getState().applicationReducer.email);
      setSignedCourses(store.getState().applicationReducer.signedCourses);
    });
    console.log(store.getState());
  });

  const logoff = () => {
    history.push("/");
  };

  return (
    <S.Container className="container">
      <Row>
        <Col md="6">
          <S.Label>Name</S.Label>
          <S.UserData>{name}</S.UserData>
        </Col>
        <Col md="6">
          <S.Label>Email</S.Label>
          <S.UserData>{email}</S.UserData>
        </Col>
      </Row>
      <S.Button color="danger" onClick={logoff}>
        Logout
      </S.Button>
    </S.Container>
  );
}
